package org.com;

public class DivisionModulo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int a=11;
         int b=2;
         int c= a/b;
         System.out.println("The output is " + c);
         
         float x=11;
         float y=2;
         float z=x/y;
         System.out.println("The output is " + z);
         
         float p=11;
         int q=2;
         float r=p/q;
         System.out.println("The output is " + r);
	}

}
